import React, { useState } from "react";
import Modal from "@mui/material/Modal";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import { Button } from "@mui/material";
import Grid from "@mui/material/Grid";

function CreateRecipeModal(props) {
  const [count, setCount] = useState(1);

  const handleClose = () => {
    props.setShowModal(false);
  };

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 3,
  };

  const ingredients = [];

  for (let i = 1; i <= count; i++) {
    ingredients.push(<br />);
    ingredients.push(
      <Grid container spacing={1}>
        <Grid item xs={4}>
          <TextField label={`Ingredient-${i}`} />
        </Grid>
        <Grid item xs={4}>
          <TextField inputProps={{ inputMode: "numeric", pattern: "[0-9]*" }} />

          <TextField inputProps={{ inputMode: "numeric", pattern: "" }} />
        </Grid>
        <Grid item xs={4}>
          <TextField label={`Unit-${i}`} />
        </Grid>
      </Grid>
    );
  }
  return (
    <Modal open={props.showModal} onClose={handleClose}>
      <Box sx={style}>
        <TextField label="Recipe Name" />
        <br />
        {ingredients}
        <br />
        <Button
          onClick={() => {
            setCount(count + 1);
          }}
        >
          +
        </Button>
        {count > 1 && (
          <Button
            onClick={() => {
              setCount(count - 1);
            }}
          >
            -
          </Button>
        )}
        <br />
        <br />
        <br />
        <Button>Create Recipe</Button>
      </Box>
    </Modal>
  );
}

export default CreateRecipeModal;
