import React, { useState } from "react";
import Modal from "@mui/material/Modal";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";

function CreateRecipeModal(props) {
  const [numberOfIngredients, setNumberOfIngredients] = useState(1);

  const handleClose = () => {
    props.setShowModal(false);
  };

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    height: 100,
    width: 450,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 3,
  };

  const ingredients = [];

  for (let i = 1; i < numberOfIngredients; i++) {
    ingredients.push(<TextField label="Ingedrient" />);
  }

  return (
    <Modal open={props.showModal} onClose={handleClose}>
      <Box sx={style}>
        <TextField label="Recipe Name" />
        <br />
        {ingredients}
      </Box>
    </Modal>
  );
}

export default CreateRecipeModal;
